import React from 'react';
import { useEffect, useState } from 'react';
import './ListarCliente.css';
import '../App.css';

function ListarCliente() {
  const [cliente, setCliente] = useState([])

  const itens = tarefas.map(function (item) {
    return (
      <div key={item.id} className="global">
        <div className="itemTarefaAdd">
          <input type="checkbox" />
          <div className="titulo" >{item.titulo} - Projeto {item.nome} </div>

          <button>apagar</button>
        </div>
      </div>
    );
  });

  useEffect(function () {
    listaTarefa()
  }, [])

  const clienteHttp = axios.create({
    baseURL: 'http://localhost:3004/'
  });

  function listaTarefa() {

    clienteHttp.get('tarefa/listar').then(function (response) {
      console.log(response.data)
      setTarefas(response.data)
    });
  }

  return (
    <div className="tabelaCliente">
      <table className="tituloTabela">
        <tr className="topoCliente">
          <th className="nomeCliente">Nome</th>
          <th className="sobrenomeCliente">Sobrenome</th>
          <th className="cpfCliente">CPF</th>
          <th className="cepCliente">CEP</th>
          <th className="endCliente">Endereço</th>
          <th className="numeroCliente">Numero</th>
          <th className="compleCliente">Complemento</th>
          <th className="bairroCliente">Bairro</th>
          <th className="cidadeCliente">Cidade</th>
          <th className="estadoCliente">Estado</th>
          <th></th>
        </tr>
      </table>
      <table className="bodyCliente">
        <tr>
          <td className="nomeCliente">Washington</td>
          <td className="sobrenomeCliente">Almeida de Figueiredo Cruz</td>
          <td className="cpfCliente">552.413.682-14</td>
          <td className="cepCliente">35.652-325</td>
          <td className="endCliente">Rua Professor Theotonio Monteiro De Barros Filhos</td>
          <td className="numeroCliente">2586</td>
          <td className="compleCliente">quadra 32 lote 67</td>
          <td className="bairroCliente">Lugar Nenhum</td>
          <td className="cidadeCliente">Geraldina</td>
          <td className="estadoCliente">São Paulo</td>
          <button className="Btm">Apagar</button>
        </tr>
        <tr>
          <td></td>
          <td>Francisco Chang</td>
          <td>Mexico</td>
        </tr>
      </table>
    </div>

  );
}

export default ListarCliente;
