import React from 'react';
import './ListarUsuario.css';
import { useEffect, useState } from 'react';
import axios from 'axios';

function listarUsuario() {
  const [usuario, setUsuario] = useState('[]')

  const itens = usuario.map(function (item) {
    return (
      <tr key={item.id}>
        <td>{item.nome}</td>
        <td>{item.email}</td>
        <td>{item.status}</td>
      </tr>


    );
  });

  useEffect(function () {
    listaUsuario()
  }, [])

  const clienteHttp = axios.create({
    baseURL: 'http://localhost:3004/'
  });

  function listaUsuario() {

    clienteHttp.get('usuario/listar').then(function (response) {
      console.log(response.data)
      setUsuario(response.data)
    });
  }

  return (
    <div className="tabelaUsuario">
      <table>
        <tr>
          <th className="nomeUsuario">Nome</th>
          <th className="emailUsuario">E-mail</th>
          <th className="statusUsuario">Status</th>
        </tr>
        <tr>
        </tr>
        <tr>
        </tr>
        {itens}
        <tr>
          <td>Centro comercial Moctezuma</td>
          <td>Francisco Chang</td>
          <td>Mexico</td>
        </tr>
      </table>
    </div>

  );
}

export default listarUsuario;
